
#include <iostream>
#include <chrono>
#include <thread>
#include <filesystem>
#include <boost/asio.hpp>
#include <boost/filesystem.hpp>
#include <boost/bind/bind.hpp>
#include <unordered_set>

namespace fs = boost::filesystem;
namespace asio = boost::asio;

class FileSystemWatcher {
public:
    FileSystemWatcher(asio::io_context& io, const std::string& path)
        : timer_(io), watch_path_(path), running_(false) {
        if (fs::exists(watch_path_) && fs::is_directory(watch_path_)) {
            cache_directory_state();
        }
    }

    void start() {
        if (running_) return;
        running_ = true;
        schedule_next_check();
    }

    void stop() {
        running_ = false;
        timer_.cancel();
    }

    void on_file_changed(std::function<void(const std::string&)> callback) {
        change_callback_ = callback;
    }

private:
    void cache_directory_state() {
        file_cache_.clear();
        for (const auto& entry : fs::recursive_directory_iterator(watch_path_)) {
            if (fs::is_regular_file(entry.path())) {
                auto last_write = fs::last_write_time(entry.path());
                file_cache_[entry.path().string()] = last_write;
            }
        }
    }

    void schedule_next_check() {
        timer_.expires_after(std::chrono::seconds(2));
        timer_.async_wait([this](const boost::system::error_code& ec) {
            if (!ec && running_) {
                check_for_changes();
                schedule_next_check();
            }
        });
    }

    void check_for_changes() {
        std::unordered_set<std::string> current_files;
        std::vector<std::string> changed_files;
        std::vector<std::string> new_files;

        for (const auto& entry : fs::recursive_directory_iterator(watch_path_)) {
            if (fs::is_regular_file(entry.path())) {
                std::string file_path = entry.path().string();
                current_files.insert(file_path);

                auto current_time = fs::last_write_time(entry.path());
                auto it = file_cache_.find(file_path);

                if (it == file_cache_.end()) {
                    new_files.push_back(file_path);
                    file_cache_[file_path] = current_time;
                } else if (it->second != current_time) {
                    changed_files.push_back(file_path);
                    it->second = current_time;
                }
            }
        }

        std::vector<std::string> deleted_files;
        for (auto it = file_cache_.begin(); it != file_cache_.end();) {
            if (current_files.find(it->first) == current_files.end()) {
                deleted_files.push_back(it->first);
                it = file_cache_.erase(it);
            } else {
                ++it;
            }
        }

        if (!new_files.empty() && change_callback_) {
            for (const auto& file : new_files) {
                change_callback_("Created: " + file);
            }
        }

        if (!changed_files.empty() && change_callback_) {
            for (const auto& file : changed_files) {
                change_callback_("Modified: " + file);
            }
        }

        if (!deleted_files.empty() && change_callback_) {
            for (const auto& file : deleted_files) {
                change_callback_("Deleted: " + file);
            }
        }
    }

    asio::steady_timer timer_;
    std::string watch_path_;
    std::unordered_map<std::string, fs::file_time_type> file_cache_;
    std::function<void(const std::string&)> change_callback_;
    bool running_;
};

int main() {
    try {
        asio::io_context io;
        FileSystemWatcher watcher(io, ".");

        watcher.on_file_changed([](const std::string& message) {
            std::cout << "[FileSystemWatcher] " << message << std::endl;
        });

        watcher.start();

        std::thread io_thread([&io]() {
            io.run();
        });

        std::cout << "Watching current directory. Press Enter to stop..." << std::endl;
        std::cin.get();

        watcher.stop();
        io.stop();
        io_thread.join();

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}